import{_ as t,c as e,o as d,aj as l}from"./chunks/framework.MtKKuSHf.js";const f=JSON.parse('{"title":"Shortcuts","description":"","frontmatter":{},"headers":[],"relativePath":"reference/scrcpy/shortcuts.md","filePath":"en/reference/scrcpy/shortcuts.md","lastUpdated":1750385449000}'),r={name:"reference/scrcpy/shortcuts.md"},s=l(`<h1 id="shortcuts" tabindex="-1">Shortcuts <a class="header-anchor" href="#shortcuts" aria-label="Permalink to &quot;Shortcuts&quot;">​</a></h1><p>Actions can be performed on the scrcpy window using keyboard and mouse shortcuts.</p><p>In the following list, <kbd>MOD</kbd> is the shortcut modifier. By default, it&#39;s (left) <kbd>Alt</kbd> or (left) <kbd>Super</kbd>.</p><p>It can be changed using <code>--shortcut-mod</code>. Possible keys are <code>lctrl</code>, <code>rctrl</code>, <code>lalt</code>, <code>ralt</code>, <code>lsuper</code> and <code>rsuper</code>. For example:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># use RCtrl for shortcuts</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">scrcpy</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --shortcut-mod=rctrl</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># use either LCtrl or LSuper for shortcuts</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">scrcpy</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --shortcut-mod=lctrl,lsuper</span></span></code></pre></div><p><em><kbd><a href="https://en.wikipedia.org/wiki/Super_key_(keyboard_button)" target="_blank" rel="noreferrer">Super</a></kbd> is typically the <kbd>Windows</kbd> or <kbd>Cmd</kbd> key.</em></p><table tabindex="0"><thead><tr><th>Action</th><th style="text-align:left;">Shortcut</th></tr></thead><tbody><tr><td>Switch fullscreen mode</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>f</kbd></td></tr><tr><td>Rotate display left</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>←</kbd> <em>(left)</em></td></tr><tr><td>Rotate display right</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>→</kbd> <em>(right)</em></td></tr><tr><td>Flip display horizontally</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>←</kbd> <em>(left)</em> | <kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>→</kbd> <em>(right)</em></td></tr><tr><td>Flip display vertically</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>↑</kbd> <em>(up)</em> | <kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>↓</kbd> <em>(down)</em></td></tr><tr><td>Pause or re-pause display</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>z</kbd></td></tr><tr><td>Unpause display</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>z</kbd></td></tr><tr><td>Reset video capture/encoding</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>r</kbd></td></tr><tr><td>Resize window to 1:1 (pixel-perfect)</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>g</kbd></td></tr><tr><td>Resize window to remove black borders</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>w</kbd> | <em>Double-left-click¹</em></td></tr><tr><td>Click on <code>HOME</code></td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>h</kbd> | <em>Middle-click</em></td></tr><tr><td>Click on <code>BACK</code></td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>b</kbd> | <kbd>MOD</kbd>+<kbd>Backspace</kbd> | <em>Right-click²</em></td></tr><tr><td>Click on <code>APP_SWITCH</code></td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>s</kbd> | <em>4th-click³</em></td></tr><tr><td>Click on <code>MENU</code> (unlock screen)⁴</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>m</kbd></td></tr><tr><td>Click on <code>VOLUME_UP</code></td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>↑</kbd> <em>(up)</em></td></tr><tr><td>Click on <code>VOLUME_DOWN</code></td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>↓</kbd> <em>(down)</em></td></tr><tr><td>Click on <code>POWER</code></td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>p</kbd></td></tr><tr><td>Power on</td><td style="text-align:left;"><em>Right-click²</em></td></tr><tr><td>Turn device screen off (keep mirroring)</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>o</kbd></td></tr><tr><td>Turn device screen on</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>o</kbd></td></tr><tr><td>Rotate device screen</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>r</kbd></td></tr><tr><td>Expand notification panel</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>n</kbd> | <em>5th-click³</em></td></tr><tr><td>Expand settings panel</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>n</kbd>+<kbd>n</kbd> | <em>Double-5th-click³</em></td></tr><tr><td>Collapse panels</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>n</kbd></td></tr><tr><td>Copy to clipboard⁵</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>c</kbd></td></tr><tr><td>Cut to clipboard⁵</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>x</kbd></td></tr><tr><td>Synchronize clipboards and paste⁵</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>v</kbd></td></tr><tr><td>Inject computer clipboard text</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>Shift</kbd>+<kbd>v</kbd></td></tr><tr><td>Open keyboard settings (HID keyboard only)</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>k</kbd></td></tr><tr><td>Enable/disable FPS counter (on stdout)</td><td style="text-align:left;"><kbd>MOD</kbd>+<kbd>i</kbd></td></tr><tr><td>Pinch-to-zoom/rotate</td><td style="text-align:left;"><kbd>Ctrl</kbd>+<em>click-and-move</em></td></tr><tr><td>Tilt vertically (slide with 2 fingers)</td><td style="text-align:left;"><kbd>Shift</kbd>+<em>click-and-move</em></td></tr><tr><td>Tilt horizontally (slide with 2 fingers)</td><td style="text-align:left;"><kbd>Ctrl</kbd>+<kbd>Shift</kbd>+<em>click-and-move</em></td></tr><tr><td>Drag &amp; drop APK file</td><td style="text-align:left;">Install APK from computer</td></tr><tr><td>Drag &amp; drop non-APK file</td><td style="text-align:left;"><a href="/reference/scrcpy/control#push-file-to-device">Push file to device</a></td></tr></tbody></table><p><em>¹Double-click on black borders to remove them.</em><br><em>²Right-click turns the screen on if it was off, presses BACK otherwise.</em><br><em>³4th and 5th mouse buttons, if your mouse has them.</em><br><em>⁴For react-native apps in development, <code>MENU</code> triggers development menu.</em><br><em>⁵Only on Android &gt;= 7.</em></p><p>Shortcuts with repeated keys are executed by releasing and pressing the key a second time. For example, to execute &quot;Expand settings panel&quot;:</p><ol><li>Press and keep pressing <kbd>MOD</kbd>.</li><li>Then double-press <kbd>n</kbd>.</li><li>Finally, release <kbd>MOD</kbd>.</li></ol><p>All <kbd>Ctrl</kbd>+<em>key</em> shortcuts are forwarded to the device, so they are handled by the active application.</p>`,11),k=[s];function i(b,a,o,n,c,p){return d(),e("div",null,k)}const m=t(r,[["render",i]]);export{f as __pageData,m as default};
