import{_ as e,c as a,o as r,aj as t}from"./chunks/framework.MtKKuSHf.js";const m=JSON.parse('{"title":"快速上手","description":"","frontmatter":{"title":"快速上手"},"headers":[],"relativePath":"zhHans/guide/started.md","filePath":"zhHans/guide/started.md","lastUpdated":1747291551000}'),o={name:"zhHans/guide/started.md"},l=t('<h1 id="快速上手" tabindex="-1">快速上手 <a class="header-anchor" href="#快速上手" aria-label="Permalink to &quot;快速上手&quot;">​</a></h1><h2 id="安装说明" tabindex="-1">安装说明 <a class="header-anchor" href="#安装说明" aria-label="Permalink to &quot;安装说明&quot;">​</a></h2><h3 id="手动安装-通过发行包" tabindex="-1">手动安装（通过发行包） <a class="header-anchor" href="#手动安装-通过发行包" aria-label="Permalink to &quot;手动安装（通过发行包）&quot;">​</a></h3><ul><li><a href="https://github.com/viarotel-org/escrcpy/releases" target="_blank" rel="noreferrer">Github发布页</a></li><li><a href="https://gitee.com/viarotel-org/escrcpy/releases" target="_blank" rel="noreferrer">Gitee发布页</a></li><li><a href="https://gitcode.com/viarotel-org/escrcpy/releases" target="_blank" rel="noreferrer">Gitcode发布页</a></li></ul><h3 id="macos用户可通过homebrew安装" tabindex="-1">macOS用户可通过Homebrew安装 <a class="header-anchor" href="#macos用户可通过homebrew安装" aria-label="Permalink to &quot;macOS用户可通过Homebrew安装&quot;">​</a></h3><p>详见 <a href="https://github.com/viarotel-org/homebrew-escrcpy" target="_blank" rel="noreferrer">homebrew-escrcpy</a></p><h2 id="usb连接方式" tabindex="-1">USB连接方式 <a class="header-anchor" href="#usb连接方式" aria-label="Permalink to &quot;USB连接方式&quot;">​</a></h2><blockquote><p>注意：若手机弹出调试授权提示，请点击允许</p></blockquote><ol><li>在安卓设备上<a href="https://www.bing.com/search?q=%E5%90%AF%E7%94%A8%E5%AE%89%E5%8D%93%E5%BC%80%E5%8F%91%E8%80%85%E6%A8%A1%E5%BC%8F" target="_blank" rel="noreferrer">启用开发者模式</a>和<a href="https://www.bing.com/search?q=%E5%90%AF%E7%94%A8%E5%AE%89%E5%8D%93USB%E8%B0%83%E8%AF%95" target="_blank" rel="noreferrer">USB调试</a>功能</li><li>启动Escrcpy并将安卓设备通过USB连接电脑</li><li>Escrcpy设备列表应已检测到您的设备，点击&quot;开始镜像&quot;</li><li>开始使用！</li></ol><h2 id="无线连接方式" tabindex="-1">无线连接方式 <a class="header-anchor" href="#无线连接方式" aria-label="Permalink to &quot;无线连接方式&quot;">​</a></h2><h3 id="扫码连接" tabindex="-1">扫码连接 <a class="header-anchor" href="#扫码连接" aria-label="Permalink to &quot;扫码连接&quot;">​</a></h3><ol><li>先完成USB连接方式的步骤1-2</li><li>在开发者选项中启用并进入&quot;无线调试&quot;</li><li>点击&quot;通过二维码配对设备&quot;</li><li>开始使用！</li></ol><h3 id="ip地址连接" tabindex="-1">IP地址连接 <a class="header-anchor" href="#ip地址连接" aria-label="Permalink to &quot;IP地址连接&quot;">​</a></h3><blockquote><p>注意：若首次无线连接失败，可能需先进行无线配对，详见<a href="/help/">常见问题</a></p><p>注意：需在无线调试页面获取设备无线地址（通常为连接WiFi时分配的IP地址）和端口号（默认为5555）</p></blockquote><ol><li>先完成USB连接方式的步骤1-2</li><li>在Escrcpy中输入设备IP地址和端口号，点击&quot;连接设备&quot;</li><li>此时设备列表应显示您的手机，点击&quot;开始镜像&quot;</li><li>开始使用！</li></ol><h2 id="macos与linux平台" tabindex="-1">macOS与Linux平台 <a class="header-anchor" href="#macos与linux平台" aria-label="Permalink to &quot;macOS与Linux平台&quot;">​</a></h2><blockquote><p>注意：这些平台未预装<a href="/reference/scrcpy/">Scrcpy</a>，需手动安装</p></blockquote><p><strong>Escrcpy@1.27.1+版本已初步集成scrcpy二进制文件，无需手动安装</strong></p><ol><li>Linux用户参考<a href="/reference/scrcpy/linux">安装文档</a></li><li>macOS用户参考<a href="/reference/scrcpy/macos">安装文档</a></li><li>依赖安装成功后，按照USB连接或无线连接的步骤操作即可</li></ol><h2 id="gnirehtet反向网络共享" tabindex="-1">Gnirehtet反向网络共享 <a class="header-anchor" href="#gnirehtet反向网络共享" aria-label="Permalink to &quot;Gnirehtet反向网络共享&quot;">​</a></h2><blockquote><p>Windows和Linux应用已内置Gnirehtet功能，可实现PC到安卓设备的网络共享。</p></blockquote><p>设备连接成功后，通过<code>设备</code>-&gt;<code>设备控制栏</code>-&gt;<code>Gnirehtet</code>启用反向网络功能。</p><p><strong>注意：macOS版本未内置Gnirehtet，需手动安装才能使用该功能<a href="/reference/gnirehtet/">安装指南</a></strong></p>',23),i=[l];function c(h,n,s,d,u,p){return r(),a("div",null,i)}const q=e(o,[["render",c]]);export{m as __pageData,q as default};
